function App () {

    const [darkMode, setDarkMode] = React.useState((localStorage.getItem("dark") === "true") && true || false),
    [screen, setScreen] = React.useState("begin"),
    [copied, setCopied] = React.useState(false),
    [results, setResults] = React.useState({words:[], answers:[], matches:0});

    return (
        <div className = {`main-container${(darkMode && " dark" || "")}`}>
            <div className = "content-wrap">
                <div className = "head">
                    <input id = "darkmode" type = "checkbox" style = {{display: "none"}} onClick = {() => {localStorage.setItem("dark", !darkMode);setDarkMode(!darkMode)}} checked = {darkMode}></input>
                    <label for = "darkmode" className = "darkmode">
                        <i></i>
                        <i></i>
                        <div className = "darkmode-ball"></div>
                    </label>
                    <div className = "title">
                        <h1>Jumble Type</h1>
                        <h2>Test how many words you can remember!</h2>
                    </div>
                </div>
                <div className = "jumble-wrap">
                    <div className = "jumble">
                        <div className = {`jumble-begin jumble-content ${(screen === "begin" && "show" || "hide")}`}>
                            <div className = "rules">       
                                <h3>How it works</h3>
                                <p>You'll be shown 10 words</p>
                                <p>Remember them as best you can</p>
                                <p>Write them back down in any order</p>
                            </div>
                            <button className = "begin" onClick = {() => {setScreen("play")}}>Get Started</button>
                        </div>
                        <Jumble screen = {screen} started = {screen === "play"} setScreen = {setScreen} setResults = {setResults}/>
                        <div className = {`jumble-end jumble-content ${(screen === "end" && "show" || "hide")}`}>
                            <h1>Score: {results.matches}/10</h1>
                            <div className = "score">{results.words.map(word => (
                                <p className = "result-word">{word}<img src = {`/images/jumbletype/${(results.answers.includes(word) && "correct" || "incorrect")}.png`}></img></p>
                            ))}</div>
                            <div className = "end-menu"><button onClick = {() => {setScreen("begin")}}>Return<img src = "/images/jumbletype/return.png"></img></button><button onClick = {() => {
                                let text = "Jumble Type Results: \n";
                                for (let word of results.words) {results.answers.includes(word) ? text += "✅ " : text += "❌ "}
                                text += "\nTry and beat my score: https://sheepstar02.github.io/jumbletype";
                                navigator.clipboard.writeText(text);
                                setCopied(true);
                            }}>{(!copied && "Share" || "Copied")}<img src = "/images/jumbletype/share.png"></img></button></div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

class Jumble extends React.Component {
    constructor (props) {
        super (props);
        this.state = {
            instructions: ["Get ready...", "Remember these words", "Ready?", "3", "2", "1", "Write down what you've remembered"],
            words: [],
            answers: [],
            instructionNum : 0,
            wordNum: 0,
            started: false,
            delay: 3000,
            input: React.createRef(),
            showGiveUp: false,
        }
    }

    generateWords () {
        for (let i = 0; i < 10; i++) {
            do {var rand = Math.floor(Math.random() * 1000)} while (this.state.words.includes(jumbleWordbank[rand]));
            this.state.words.push(jumbleWordbank[rand].toUpperCase());
        }
    }

    loadGame () {
        switch (this.state.instructionNum) {
            case 1:
                this.state.delay = 10000;
                setTimeout(() => {this.updateWord()}, 1000)
                break;
            case 2:
                this.state.delay = 1000;
                break;
            case 6:
                return;
        }

        setTimeout(() => {
            this.props.started ? this.setState({instructionNum: this.state.instructionNum+1}) : this.state.instructions;
            this.loadGame()
        }, this.state.delay);
    }

    handleKeyPress = (event) => {
        if (event.code === "Enter" && this.state.input.current.value.length > 0) {
            this.setState({showGiveUp:false});
            this.state.answers.push(this.state.input.current.value.trim().toUpperCase());
            this.state.input.current.value = "";
            if (this.state.answers.length >= 10) {
                this.completeGame()
            }
        }
    }

    completeGame () {
        let score = 0;
        for (let word of this.state.words) {if (this.state.answers.includes(word)) score++}
        this.props.setResults({answers:this.state.answers,words:this.state.words,matches:score})
        this.props.setScreen("end");
        this.setState({words: [],answers: [],instructionNum : 0,wordNum: 0,started: false,delay: 3000});
    }

    updateWord () {
        if (this.state.wordNum >= 9) {return}
        this.setState({wordNum: this.state.wordNum+1})
        setTimeout(() => {this.updateWord()}, 1000);
    }

    render () {

        if (!this.state.showGiveUp) {setTimeout(() => {this.setState({showGiveUp:true})}, 10)}

        if (this.props.started && !this.state.started) {
            this.state.started = true;
            this.generateWords();
            this.loadGame();
        }

        return (
            <div className = {`jumble-play jumble-content ${(this.props.screen === "play" && "show" || "hide")} phase-${((this.state.instructionNum >= 1 && !(this.state.instructionNum < 6 && this.state.instructionNum > 1)) && (this.state.instructionNum >= 6 && "answer" || "show") || "none")}`}>
                <div className = "instructions">
                    <h1>{this.state.instructions[this.state.instructionNum]}</h1>
                </div>
                <div className = "jumble-input">
                    <h1>{this.state.words[this.state.wordNum]}</h1>
                    <div className = "word-inputs">
                        <input ref = {this.state.input} type = "text" maxLength={11} placeholder = "TYPE HERE" onKeyPress = {this.handleKeyPress}></input>
                        <button className = {(this.state.showGiveUp && "fadeIn" || "")} onClick = {() => {this.completeGame()}}>Give up?</button>
                    </div>
                </div>
            </div>
        )
    }
}

ReactDOM.render(
    <App />,
    document.getElementById("root")
)